import React from 'react';
import './SortingCodeImages.css';

function SortingCodeImages({ selectedAlgorithm, setSelectedAlgorithm }) {

 // Mapping of algorithm names to their corresponding image filenames
 const algorithmImages = {
 bubble: 'bubble.png',
 insertion: 'insertion.png',
 quick: 'quick.png',
 selection: 'selection.png',
 merge: 'merge.png'
 };

 const handleAlgorithmChange = (event) => {
 setSelectedAlgorithm(event.target.value);
 };

 return (
 <div className="sorting-code">
 <select className="algorithm-dropdown" value={selectedAlgorithm} onChange={handleAlgorithmChange}>
 {Object.keys(algorithmImages).map(algorithm => (
 <option key={algorithm} value={algorithm}>
 {algorithm.charAt(0).toUpperCase() + algorithm.slice(1)} Sort
 </option>
 ))}
 </select>
 <div className="algorithm-code">
 <div className="algorithm">
 <img src={algorithmImages[selectedAlgorithm]} alt={`${selectedAlgorithm} Sort Code`} />
 </div>
 </div>
 </div>
 );

 
 
}

export default SortingCodeImages;
